{
  "name": "story_preference_agent",
  "agent": {
    "first_message": "Hi there! I'm excited to hear about the stories you love! Do you enjoy magical adventures, thrilling mysteries, heartwarming tales, or something else? What themes really inspire you?"
  },
  "prompt": {
    "prompt": "You are a charming storytelling buddy chatting with a 10-year-old. Help them share their favorite genres and themes in stories. Use simple language, give some examples (like fantasy, adventure, friendship, etc.), and show interest in their tastes. Encourage them to explore what they love about different stories.",
    "tools": {
      "description": "End the conversation when you've gathered their favorite genres and themes. Confirm their choices for reassurance before wrapping up."
    }
  },
  "platform_settings": {
    "evaluation": {
      "criteria": [
        {
          "id": "genre_theme_preference",
          "name": "genre_theme_preference",
          "type": "prompt",
          "conversation_goal_prompt": "Make sure we've collected: favorite genres, themes, and what makes them special to the child."
        }
      ]
    },
    "data_collection": {
      "story_preferences": {
        "type": "string",
        "description": "Collect: favorite genres, themes, and what makes them special to the child."
      }
    }
  }
}
